import styled from "styled-components";

const Item = styled.div`
  .bg-pwhite;
  .w-full;
  .lg: w-3/12;
  box-sizing: border-box;
  .p-4;
  .shadow;
  .mx-2;
  .my-4;
`;

const Image = styled.img`
  height: 100px;
  max-height: 100px;
  width: 100%;
  object-fit: cover;
`;

const Title = styled.div`
  .text-2xl;
  font-weight: bold;
`;

const List = styled.div`
  .flex;
  .mt-4;
  .items-center;
`;

const Icon = styled.img`
  height: 25px;
  .mr-4;
`;

// title: "Let U(I) be optimistic",
//     host: "GraphQL HongKong",
//     date: "14 May, 2020",
//     desc: "A talk and demo on Optimistic UI, types and real world examples using GraphQL",
//     slides: "https://slides.com/vilvaathiban/deck-969a80",
//     video: "https://youtu.be/uvKG1AmrXDw",
//     code: ""

const EntityComponent = ({ data }) => {
  const { title, host, date, desc, slides, video, code } = data;
  return (
    <Item>
      <Title>{title}</Title>
      {desc && <List>{desc}</List>}
      {host && <List>{host}</List>}
      {date && <List>{date}</List>}
      {slides && <List><a href={slides}><Icon src="/icons/slides.svg" alt="Link to Slides"/>Slides</a></List>}
      {video && <List><a href={video}><Icon src="/icons/video.svg" alt="Link to Slides"/>Video</a></List>}
      {code && <List><a href={code}><Icon src="/icons/code.svg" alt="Link to Slides"/>Demo code</a></List>}
    </Item>
  );
};

export default EntityComponent;
