import styled from "styled-components";

const Item = styled.div`
  .bg-pwhite;
  .w-full;
  .lg: w-3/12;
  box-sizing: border-box;
  .p-4;
  .shadow;
  .mx-2;
  .my-4;
`;

const Image = styled.img`
  height: 100px;
  max-height: 100px;
  width: 100%;
  object-fit: cover;
`;

const Title = styled.div`
  .text-2xl;
  font-weight: bold;
  overflow:hidden;
`;

const List = styled.div`
  .flex;
  .mt-4;
  .items-center;
`;

const Icon = styled.img`
  height: 25px;
  .mr-4;
`;

const Embed = styled.embed`
  .w-full;
`;

const Thumbnail = styled.img`
  .w-full;
`;

const EntityComponent = ({ data }) => {
  const { title, host, date, desc, slides, video, code, url, embed, knowMore, buy, thumbnail } = data;
  return (
    <Item>
      {embed && <Embed src={embed} />}
      {thumbnail && <Thumbnail src={thumbnail} alt={title} />}
      <Title>{title}</Title>
      {desc && <List>{desc}</List>}
      {host && <List style={{fontWeight: 700}}>{host}</List>}
      {date && <List>{date}</List>}
      {slides && (
        <a href={slides} target="_blank" rel="noopener noreferrer">
          <List>
            <Icon src="/icons/slides.svg" alt="Link to Slides" />
            Slides
          </List>
        </a>
      )}
      {video && (
        <a href={video} target="_blank" rel="noopener noreferrer">
          <List>
            <Icon src="/icons/video.svg" alt="Link to Slides" />
            Video
          </List>
        </a>
      )}
      {code && (
        <a href={code} target="_blank" rel="noopener noreferrer">
          <List>
            <Icon src="/icons/code.svg" alt="Link to Slides" />
            Demo code
          </List>
        </a>
      )}
      {url && (
        <a href={url} target="_blank" rel="noopener noreferrer">
          <List>
            <Icon src="/icons/url.svg" alt="Link to Slides" />
            Read blog
          </List>
        </a>
      )}
    </Item>
  );
};

export default EntityComponent;
