import styled from "styled-components";
import Link from "next/link";
import { useState } from "react";
import SideMenu from "../SideMenu";

const Container = styled.div`
  .flex;
  .justify-between;
  .px-4;
  .lg: px-12;
  .py-4;
  .items-center;
`;
const LogoContainer = styled.div``;
const Logo = styled.img`
  height: 100px;
`;

const MenuContainer = styled.div`
  .hidden;
  .lg: flex;
  .justify-between;
  .align-center;
  .w-1/2;
  .text-pwhite;
  font-size: 18px;
`;

const MobileMenu = styled.div`
  .lg: hidden;
  .text-4xl;
  .cursor-pointer;
`;

const Menu = styled.div`
    .cursor-pointer
`;

const Header = () => {
  const [showMenu, toggleMenu] = useState(false);
  return (
    <Container>
      <LogoContainer>
        <Link href="/">
          <Logo src="/logo.png" alt="Vilva Athiban P B, The JS Jockey" />
        </Link>
      </LogoContainer>
      <MenuContainer>
        <Link href="/about">
          <Menu>About</Menu>
        </Link>
        <Menu>Talks</Menu>
        <Menu>Blogs</Menu>
        <Menu>Videos</Menu>
        <Menu>Projects</Menu>
        <Menu>Get in Touch</Menu>
      </MenuContainer>
      <MobileMenu onClick={() => toggleMenu(true)}>üçî</MobileMenu>
      <SideMenu closeMenu={toggleMenu} showMenu={showMenu} />
    </Container>
  );
};

export default Header;
