import styled from "styled-components";

const MenuItems = styled.div`
  .bg-pwhite;
  width: 100vw;
  height: 100vh;
  overflow-y: scroll;
  .fixed;
  .top-0;
  .left-0;
  .text-pdark;
  .flex;
  .flex-col;
  .justify-around;
  .items-center;
  font-size: 18px;
  font-weight: 700;
  animation: show-menu 1s;

  @keyframes show-menu {
    0% {
      left: 100%;
    }
    100% {
      left: 0;
    }
  }
`;

const Close = styled.div`
  .lg: hidden;
  .absolute;
  .top-0;
  .right-0;
  .px-8;
  .py-4;
`;

const Menu = styled.div``;

const SideMenu = ({ showMenu, closeMenu }) => {
  return (
    <>
      {showMenu && (
        <MenuItems>
          <Close onClick={() => closeMenu(false)}>X</Close>
          <Menu>About</Menu>
          <Menu>Talks</Menu>
          <Menu>Blogs</Menu>
          <Menu>Videos</Menu>
          <Menu>Projects</Menu>
          <Menu>Get in Touch</Menu>
        </MenuItems>
      )}
    </>
  );
};

export default SideMenu;
